<?phprequire_once(dirname(__FILE__).'/../nusoap-0.7.3/nusoap.php');class JHSFWebService {	public static $jhsfSchema = 'http://www.harvestroad.com/hive/jhsf/schema/api/jhsf';		public static function getClient($hwsURI){		return new nusoap_client(trim($hwsURI).'/public/soap/v4/jhsf',false);	}		public static function getToken($soapClient)	{		return $soapClient->call('getToken',array(),self::$jhsfSchema);	}	}?>